import React from "react";
import "./ExpenseForm.css";
import { useState } from "react";
const ExpenseForm = ({getData}) => {
    const  [userInput, setUserInput] = useState({
        enteredTitle: "",
        entredPrice: "",
        enteredDate: "",
    });
    const titleChangeHandler = (event) => {
        setUserInput((prevState) => {
            return {...prevState, enteredTitle: event.target.value};
        });
    };
    const priceChangeHandler = (event) => {
        setUserInput((prevState) => {
            return{...prevState, entredPrice: event.target.value};
        });
    };
      const dateChangeHandler = (event) => {
    setUserInput((prevState) => {
      return { ...prevState, entredDate: event.target.value };
    });
  };
  const submitHandler = (event) => {
    event.preventDefault();
    // getData(userInput);
    const exp = {
            title: userInput.enteredTitle,
            price: userInput.entredPrice,
            date: userInput.enteredDate,
        }
  };
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>{titleChangeHandler}</label>
          <input />
        </div>
        <div className="new-expense__control">
          <label>{priceChangeHandler}</label>
          <input />
        </div>
        <div className="new-expense__control">
          <label>dateChangeHandler}</label>
          <input />
        </div>
      </div>
      <div className="new-expense__actions">
        <button>Cancel</button>
        <button>Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
